mapper.select:
select id, user_id, upload_id, upload_time, url, status
from t_background
where user_id = #{userId}
  and status = 0;

select id, user_id, upload_id, upload_time, url, status
from t_background
where id = #{id}

mapper.update:
update t_background
set status = #{status}
where id = #{id}
